package org.functional.Patterns;

public class FirstPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=3;
		  for(int i=0;i<n;i++){
	            for(int j=0;j<n;j++){
	                System.out.print(" * ");
	            }
	            System.out.println();
	        }





	}

}
